import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

function RecipeDetailPage() {
  const { id } = useParams(); // Get the recipe ID from the URL
  const [recipe, setRecipe] = useState(null);

  useEffect(() => {
    const fetchRecipeDetails = async () => {
      try {
        const response = await axios.get(`https://api.edamam.com/api/recipes/v2/${id}`, {
          params: {
            app_id: "2924ba02", // Your Edamam App ID
            app_key: "5bddcb2069c125bfb9efe35124827718", // Your Edamam API Key
          },
        });
        setRecipe(response.data.recipe); // Set the recipe details
      } catch (error) {
        console.error("Error fetching recipe details:", error);
      }
    };

    fetchRecipeDetails();
  }, [id]); // Re-fetch when the ID changes

  if (!recipe) return <div>Loading...</div>; // Show loading if recipe is not available

  return (
    <div>
      <h1>{recipe.label}</h1>
      <img src={recipe.image} alt={recipe.label} />
      <h3>Ingredients</h3>
      <ul>
        {recipe.ingredients.map((ingredient, index) => (
          <li key={index}>{ingredient.text}</li>
        ))}
      </ul>
    </div>
  );
}

export default RecipeDetailPage;
