import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Hook for navigation
import axios from "axios";
import "./app.css";
import Loginbutton from "./loginbutton";
import Registerbutton from "./registerbutton";
import Logoutbutton from "./logoutbutton";
import Allrecipiesbutton from "./allrecepiesbutton";

const signedin = localStorage.getItem("signedin");

function Header() {
  const [query, setQuery] = useState(""); // Search bar input
  const [suggestions, setSuggestions] = useState([]); // Suggestions list
  const [loading, setLoading] = useState(false); // Loading state
  const navigate = useNavigate(); // Hook to navigate to other pages

  // Fetch recipe suggestions from the Edamam API
  const fetchSuggestions = async (searchTerm) => {
    if (!searchTerm) {
      setSuggestions([]); // Clear suggestions when input is empty
      return;
    }

    try {
      setLoading(true);

      const response = await axios.get("https://api.edamam.com/api/recipes/v2", {
        params: {
          q: searchTerm,
          type: "public",
          app_id: "2924ba02", // Your Edamam App ID
          app_key: "5bddcb2069c125bfb9efe35124827718", // Your Edamam API Key
          from: 0,
          to: 5, // Fetch top 5 suggestions
        },
      });

      const recipeSuggestions = response.data.hits.map((hit) => ({
        label: hit.recipe.label,
        image: hit.recipe.image,
        id: hit.recipe.uri.split("#")[1],  // Extract the recipe ID from URI
      }));

      setSuggestions(recipeSuggestions);
    } catch (error) {
      console.error("Error fetching suggestions:", error);
    } finally {
      setLoading(false);
    }
  };

  // Handle search bar input change
  const handleInputChange = (event) => {
    const searchTerm = event.target.value;
    setQuery(searchTerm);

    if (searchTerm.trim() === "") {
      setSuggestions([]); // Clear suggestions if input is empty
    } else {
      fetchSuggestions(searchTerm); // Fetch suggestions if input is not empty
    }
  };

  // Handle suggestion selection (navigate to recipe page)
  const handleSuggestionClick = (recipeId) => {
    navigate(`/recipe/${recipeId}`);  // Use navigate to route to recipe page
    setQuery(""); // Clear the search bar
    setSuggestions([]); // Clear suggestions
  };

  // Handle pressing Enter (optional, if you want to use Enter for search)
  const handleSearchSubmit = (event) => {
    if (event.key === "Enter" && query.trim() !== "") {
      setQuery(""); // Clear the search bar
      setSuggestions([]); // Clear suggestions
    }
  };

  return (
    <div>
      <header className="header">
        <a href="/">
          <img src="assets/logo.png" alt="Logo" className="logo" />
        </a>

        {/* Search Bar */}
        <div className="search-bar">
          <input
            type="text"
            value={query}
            onChange={handleInputChange}
            onKeyDown={handleSearchSubmit} // Handle Enter key press
            placeholder="Search"
            className="search-input"
          />
          {loading && <div className="loading">Loading...</div>}
          {suggestions.length > 0 && (
            <ul className="suggestions-dropdown">
              {suggestions.map((recipe, index) => (
                <li
                  key={index}
                  onClick={() => handleSuggestionClick(recipe.id)}  // Pass the recipe ID for navigation
                  className="suggestion-item"
                >
                  <img
                    src={recipe.image}
                    alt={recipe.label}
                    className="suggestion-image"
                  />
                  <span>{recipe.label}</span>
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Authentication Buttons */}
        <div className="auth-buttons">
          <Allrecipiesbutton />
          {signedin === "false" ? (
            <>
              <Loginbutton />
              <Registerbutton />
            </>
          ) : (
            <>
              <span>Hello, welcome again</span>
              <Logoutbutton />
            </>
          )}
        </div>
      </header>
    </div>
  );
}

export default Header;
