import React, { useEffect, useState } from 'react';
import axios from 'axios';

const RandomRecipePage = () => {
  const [recipe, setRecipe] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRandomRecipe = async () => {
      try {
        // Request to Edamam API
        const response = await axios.get('https://api.edamam.com/api/recipes/v2', {
          params: {
            q: 'random',  // This is a search query, but it could be anything (like 'random' for demo)
            type: 'public',
            app_id: '2924ba02',  // Replace with your actual Edamam App ID
            app_key: 'YOUR_API_KEY',  // Replace with your actual Edamam API Key
          },
        });

        // Extracting the first recipe from the hits
        const randomRecipe = response.data.hits[0].recipe;
        setRecipe(randomRecipe);  // Update the recipe state with the fetched recipe
      } catch (error) {
        setError('Failed to fetch recipe');
        console.error('Error fetching recipe:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchRandomRecipe();
  }, []);  // Empty dependency array ensures the fetch runs once on page load or refresh

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h1>{recipe.label}</h1>
      <img src={recipe.image} alt={recipe.label} style={{ width: '300px', height: 'auto' }} />
      <h2>Ingredients:</h2>
      <ul>
        {recipe.ingredients.map((ingredient, index) => (
          <li key={index}>{ingredient.text}</li>
        ))}
      </ul>
      <a href={recipe.url} target="_blank" rel="noopener noreferrer">
        See full recipe instructions
      </a>
    </div>
  );
};

export default RandomRecipePage;
