import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./app.css";
import Loginbutton from "./loginbutton";
import Registerbutton from "./registerbutton";
import Logoutbutton from "./logoutbutton";
import Allrecipiesbutton from "./allrecepiesbutton";

const handleClick = () => {
  window.open("/", "_self");
};

const signedin = localStorage.getItem("signedin");

function Header() {
  const [query, setQuery] = useState(""); // State pour la barre de recherche
  const [suggestions, setSuggestions] = useState([]); // Suggestions des recettes
  const [loading, setLoading] = useState(false); // Indicateur de chargement
  const navigate = useNavigate(); // Hook pour la navigation

  // Fonction pour récupérer les suggestions à partir de l'API
  const fetchSuggestions = async (searchTerm) => {
    if (!searchTerm) {
      setSuggestions([]); // Si la recherche est vide, on vide les suggestions
      return;
    }

    try {
      setLoading(true);

      const response = await axios.get("https://api.edamam.com/api/recipes/v2", {
        params: {
          q: searchTerm,
          type: "public",
          app_id: "2924ba02", // Remplacez par votre App ID
          app_key: "5bddcb2069c125bfb9efe35124827718", // Remplacez par votre API Key
          from: 0,
          to: 5, // Limitez à 5 suggestions
        },
      });

      // Préparation des suggestions
      const recipeSuggestions = response.data.hits.map((hit) => ({
        id: hit.recipe.uri.split("#")[1], // Extraction de l'ID de la recette
        label: hit.recipe.label,
        image: hit.recipe.image,
      }));

      setSuggestions(recipeSuggestions);
    } catch (error) {
      console.error("Erreur lors de la récupération des suggestions :", error);
    } finally {
      setLoading(false);
    }
  };

  // Gestion des changements dans l'input
  const handleInputChange = (event) => {
    const searchTerm = event.target.value;
    setQuery(searchTerm);

    if (searchTerm.trim() === "") {
      setSuggestions([]); // On vide les suggestions si le champ est vide
    } else {
      fetchSuggestions(searchTerm); // On récupère les suggestions sinon
    }
  };

  // Gestion du clic sur une suggestion
  const handleSuggestionClick = (recipe) => {
    navigate(`/recipe/${recipe.id}`); // Navigation vers la page de détails
    setQuery(""); // On vide la barre de recherche
    setSuggestions([]); // On vide les suggestions
  };

  return (
    <header className="header">
      {/* Logo */}
      <a onClick={handleClick}>
        <img src="assets/logo.png" alt="Logo" className="logo" />
      </a>

      {/* Barre de recherche */}
      <div className="search-bar">
        <input
          type="text"
          value={query}
          onChange={handleInputChange}
          placeholder="Search for a dish..."
          className="search-input"
        />
        {loading && <div className="loading">Loading...</div>}
        {suggestions.length > 0 && (
          <ul className="suggestions-dropdown">
            {suggestions.map((recipe, index) => (
              <li
                key={index}
                onClick={() => handleSuggestionClick(recipe)}
                className="suggestion-item"
              >
                <img
                  src={recipe.image}
                  alt={recipe.label}
                  className="suggestion-image"
                />
                <span>{recipe.label}</span>
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Boutons d'authentification */}
      <div className="auth-buttons">
        <Allrecipiesbutton />
        {signedin === "false" ? (
          <>
            <Loginbutton />
            <Registerbutton />
          </>
        ) : (
          <>
            <span>Hello, welcome again</span>
            <Logoutbutton />
          </>
        )}
      </div>
    </header>
  );
}

export default Header;
