import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";  // Use Link to navigate internally

const SearchBar = () => {
  const [query, setQuery] = useState(""); // Stores the search query
  const [suggestions, setSuggestions] = useState([]); // Stores recipe suggestions
  const [selectedRecipe, setSelectedRecipe] = useState(null); // Stores the selected recipe
  const [loading, setLoading] = useState(false); // Tracks loading state

  // Fetch suggestions as the user types
  const fetchSuggestions = async (searchTerm) => {
    if (!searchTerm) {
      setSuggestions([]);
      return;
    }

    try {
      setLoading(true);

      const response = await axios.get("https://api.edamam.com/api/recipes/v2", {
        params: {
          q: searchTerm, // User's query
          type: "public",
          app_id: "2924ba02", // Your App ID
          app_key: "5bddcb2069c125bfb9efe35124827718", // Your API Key
          from: 0, // Fetch the first few results
          to: 5, // Limit to 5 suggestions
        },
      });

      // Extract recipe names and store them as suggestions
      const recipeSuggestions = response.data.hits.map((hit) => ({
        id: hit.recipe.uri.split("#")[1],  // Extract the ID from the URI
        label: hit.recipe.label,
        image: hit.recipe.image,
      }));

      setSuggestions(recipeSuggestions);
    } catch (error) {
      console.error("Error fetching suggestions:", error);
    } finally {
      setLoading(false);
    }
  };

  // Handle query change and fetch suggestions
  const handleInputChange = (event) => {
    const searchTerm = event.target.value;
    setQuery(searchTerm);
    fetchSuggestions(searchTerm);
  };

  return (
    <div style={{ textAlign: "center", margin: "20px auto", maxWidth: "600px" }}>
      {/* Search Bar */}
      <input
        type="text"
        value={query}
        onChange={handleInputChange}
        placeholder="Search for a dish..."
        style={{
          width: "100%",
          padding: "10px",
          borderRadius: "4px",
          border: "1px solid #ddd",
          fontSize: "16px",
        }}
      />

      {/* Suggestions Dropdown */}
      {suggestions.length > 0 && (
        <ul
          style={{
            listStyle: "none",
            padding: "0",
            margin: "10px 0 0",
            border: "1px solid #ddd",
            borderRadius: "4px",
            backgroundColor: "#fff",
            position: "absolute",
            width: "100%",
            zIndex: 10,
          }}
        >
          {suggestions.map((recipe, index) => (
            <li
              key={index}
              style={{
                padding: "10px",
                borderBottom: "1px solid #eee",
                cursor: "pointer",
                display: "flex",
                alignItems: "center",
                gap: "10px",
              }}
            >
              <Link to={`/recipe/${recipe.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
                <img
                  src={recipe.image}
                  alt={recipe.label}
                  style={{
                    width: "40px",
                    height: "40px",
                    borderRadius: "4px",
                    objectFit: "cover",
                  }}
                />
                <span>{recipe.label}</span>
              </Link>
            </li>
          ))}
        </ul>
      )}

      {/* Loading State */}
      {loading && <p>Loading...</p>}

      {/* Selected Recipe */}
      {selectedRecipe && (
        <div style={{ marginTop: "20px" }}>
          <h2>{selectedRecipe.label}</h2>
          <img
            src={selectedRecipe.image}
            alt={selectedRecipe.label}
            style={{ maxWidth: "100%", borderRadius: "8px" }}
          />
          <p>
            <a
              href={selectedRecipe.url}
              target="_blank"
              rel="noopener noreferrer"
              style={{
                color: "#007BFF",
                textDecoration: "none",
                fontWeight: "bold",
              }}
            >
              View Full Recipe
            </a>
          </p>
        </div>
      )}
    </div>
  );
};

export default SearchBar;
