import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./app.css";

const Header = () => {
  const [query, setQuery] = useState(""); // Search bar input
  const [suggestions, setSuggestions] = useState([]); // Suggestions list
  const [loading, setLoading] = useState(false); // Loading state
  const navigate = useNavigate(); // Navigation hook to navigate to RecipeDetailPage

  // Fetch recipe suggestions
  const fetchSuggestions = async (searchTerm) => {
    if (!searchTerm) {
      setSuggestions([]); // Clear suggestions when input is empty
      return;
    }

    try {
      setLoading(true);

      const response = await axios.get("https://api.edamam.com/api/recipes/v2", {
        params: {
          q: searchTerm,
          type: "public",
          app_id: "2924ba02", // Replace with your Edamam App ID
          app_key: "5bddcb2069c125bfb9efe35124827718", // Replace with your Edamam API Key
          from: 0,
          to: 5, // Fetch top 5 suggestions
        },
      });

      const recipeSuggestions = response.data.hits.map((hit) => ({
        label: hit.recipe.label,
        image: hit.recipe.image,
        id: hit.recipe.uri.split("#")[1], // Extract recipe ID from the URI
      }));

      setSuggestions(recipeSuggestions);
    } catch (error) {
      console.error("Error fetching suggestions:", error);
    } finally {
      setLoading(false);
    }
  };

  // Handle search bar input change
  const handleInputChange = (event) => {
    const searchTerm = event.target.value;
    setQuery(searchTerm);

    if (searchTerm.trim() === "") {
      setSuggestions([]); // Clear suggestions if input is empty
    } else {
      fetchSuggestions(searchTerm); // Fetch suggestions if input is not empty
    }
  };

  // Handle suggestion selection and navigate to RecipeDetailPage
  const handleSuggestionClick = (recipe) => {
    // Navigate to the RecipeDetailPage with the recipe ID
    navigate(`/recipe/${recipe.id}`);
    setQuery(""); // Clear the search bar
    setSuggestions([]); // Clear suggestions
  };

  return (
    <div>
      <header className="header">
        {/* Your other header components */}

        {/* Search Bar */}
        <div className="search-bar">
          <input
            type="text"
            value={query}
            onChange={handleInputChange}
            placeholder="Search"
            className="search-input"
          />
          {loading && <div className="loading">Loading...</div>}
          {suggestions.length > 0 && (
            <ul className="suggestions-dropdown">
              {suggestions.map((recipe, index) => (
                <li
                  key={index}
                  onClick={() => handleSuggestionClick(recipe)} // Navigate to recipe detail page
                  className="suggestion-item"
                >
                  <img
                    src={recipe.image}
                    alt={recipe.label}
                    className="suggestion-image"
                  />
                  <span>{recipe.label}</span>
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Authentication Buttons */}
        {/* Your authentication buttons */}
      </header>
    </div>
  );
};

export default Header;
