import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom"; // Import useParams to get the recipe ID

const RecipeDetailPage = () => {
  const { id } = useParams(); // Get the recipe ID from the URL
  const [recipe, setRecipe] = useState(null); // Stores the selected recipe data
  const [loading, setLoading] = useState(true); // Loading state

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const response = await axios.get(`https://api.edamam.com/api/recipes/v2/${id}`, {
          params: {
            app_id: "2924ba02", // Use environment variables for API credentials
            app_key: "5bddcb2069c125bfb9efe35124827718",
          },
        });
        setRecipe(response.data.recipe);
      } catch (error) {
        console.error("Error fetching recipe details:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchRecipe();
  }, [id]); // Fetch recipe details when the ID changes

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!recipe) {
    return <div>Recipe not found</div>;
  }

  return (
    <div style={{ padding: "20px" }}>
      <h1>{recipe.label}</h1>
      <img
        src={recipe.image}
        alt={recipe.label}
        style={{ width: "100%", borderRadius: "8px" }}
      />
      <h3>Ingredients</h3>
      <ul>
        {recipe.ingredients.map((ingredient, index) => (
          <li key={index}>{ingredient.text}</li>
        ))}
      </ul>
    </div>
  );
};

export default RecipeDetailPage;
