import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom"; // Use for navigation
import axios from "axios";
import "./app.css";
import Loginbutton from "./loginbutton";
import Registerbutton from "./registerbutton";
import Logoutbutton from "./logoutbutton";
import Allrecipiesbutton from "./allrecepiesbutton";

// Check if the user is signed in
const signedin = localStorage.getItem("signedin");

function Header() {
  const navigate = useNavigate(); // Hook to navigate to different pages

  // List of recipes to be displayed (Replace with your data fetching method)
  const [recipes, setRecipes] = useState([]);

  // Fetch some recipes to display in the header (just an example)
  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await axios.get("https://api.edamam.com/api/recipes/v2", {
          params: {
            q: "pasta", // You can replace this with any recipe category or query
            type: "public",
            app_id: "2924ba02", // Your Edamam App ID
            app_key: "5bddcb2069c125bfb9efe35124827718", // Your Edamam API Key
            from: 0,
            to: 5, // Limit the number of results
          },
        });

        setRecipes(response.data.hits); // Set the fetched recipes
      } catch (error) {
        console.error("Error fetching recipes:", error);
      }
    };

    fetchRecipes();
  }, []);

  // Handle navigation to RecipeDetailPage with recipe ID
  const handleRecipeClick = (recipeId) => {
    navigate(`/recipe/${recipeId}`); // Use navigate to go to the recipe detail page
  };

  return (
    <div>
      <header className="header">
        <a href="/">
          <img src="assets/logo.png" alt="Logo" className="logo" />
        </a>

        {/* Recipes List (you can replace this with your preferred list) */}
        <div className="recipe-links">
          <h3>Popular Recipes</h3>
          <ul>
            {recipes.map((recipe, index) => (
              <li key={index} onClick={() => handleRecipeClick(recipe.recipe.uri.split("#")[1])}>
                <img
                  src={recipe.recipe.image}
                  alt={recipe.recipe.label}
                  className="recipe-image"
                />
                <span>{recipe.recipe.label}</span>
              </li>
            ))}
          </ul>
        </div>

        {/* Authentication Buttons */}
        <div className="auth-buttons">
          <Allrecipiesbutton />
          {signedin === "false" ? (
            <>
              <Loginbutton />
              <Registerbutton />
            </>
          ) : (
            <>
              <span>Hello, welcome again</span>
              <Logoutbutton />
            </>
          )}
        </div>
      </header>
    </div>
  );
}

export default Header;
